clear;%set the initial dataE = 50;B = 100;%set the number of iteratesM = 200;%set the parametersk = 1.05;%a ratea = 0.95; %a = gamma from the model%store the initial datae(1) = E;b(1) = B;%iteratefor j=1:M%evaluate the functions Prob = exp(-a*E/B);E_new = a*B;B_new = k*Prob;%store the current valuese(j) = E_new;b(j) = B_new;%updateE = E_new;B = B_new;end%make some plotsfigure(1)plot([1:M],e,'b',[1:M],b,'r',[1:M],e,'b*',[1:M],b,'r*')figure(2)plot(e,b,'*')